INSERT INTO `update_cms_contente` VALUES ('c_100204-s_10008', '固安工业园区志愿服务章程（草案）', null, null, '<p style=\"text-indent: 228px; text-align: center;\"><strong><span style=\"font-size:16px;font-family:宋体\">第一章&nbsp; 总则</span></strong></p>\n<p style=\"text-indent: 32px\"><strong><span style=\"font-size:16px;font-family: 宋体\">第一条 </span></strong><span style=\"font-size: 16px;font-family: 宋体\">为倡导“奉献、友爱、互助、进步”的精神，</span><span style=\"font-size:16px;font-family:宋体\">鼓励和规范</span><span style=\";font-family:宋体\">了志愿服务者(以下简称志愿者)的服务工作</span><span style=\"font-size:16px;font-family:宋体\">活动，推动志愿服务事业的健康发展，弘扬社会主义道德风尚，促进社会和谐，</span><span style=\"font-size: 16px;font-family: 宋体\">根据宪法和有关法律、法规的规定，结合本会实际，制定本章程。</span></p>\n<p style=\"text-indent: 32px\"><strong><span style=\"font-family: 宋体;\">第二条</span></strong><span style=\"font-family: 宋体;\"> 协会内的志愿者、志愿服务组织及其活动适用本章程。</span></p>\n<p style=\"text-indent: 32px\"><strong><span style=\"font-size: 16px;font-family:宋体\"><br></span></strong></p>\n<p style=\"text-indent: 32px\"><strong><span style=\"font-size: 16px;font-family:宋体\">第三条 </span></strong><span style=\"font-size:16px;font-family:宋体\">本章程所称的志愿者，是指出于奉献、友爱、互助和社会责任，经过登记，自愿、无偿地以自己的时间、技能等资源开展社会服务和公益活动的人员。</span></p>\n<p style=\"text-indent: 40px\"><span style=\"font-size:16px;font-family:宋体\">本章程所称的志愿服务组织是指依法登记注册、专门从事志愿服务活动的非营利性社会团体法人以及从事志愿者服务活动的机关、非营利性事业单位、社会团体等团体志愿者。</span></p>\n<p style=\"text-indent: 40px\"><strong style=\"text-indent: 16px;\"><span style=\"font-family: 宋体;\">第四条</span></strong><span style=\"text-indent: 16px; font-family: 宋体;\"> 志愿服务活动应当遵循自愿、合法、诚信、节俭和非营利性的原则。</span></p>\n<p style=\"text-indent: 16px\"><strong><span style=\"font-family: 宋体;\">第五条</span></strong><span style=\"font-family: 宋体;\"> 志愿服务范围包括扶危济困、敬老助残、社区服务、秩序维护、文体服务、环境保护、 治安防范、医疗救治、法律援助、大型活动、心理疏导、精神抚慰、支教支医、科学普及、应急救援、便民服务、民事调解、文明引导、安全生产等服务内容。</span></p>\n<p style=\"text-indent: 16px\"><strong style=\"text-indent: 40px;\"><span style=\"font-family: 宋体;\">第六条</span></strong><span style=\"text-indent: 40px; font-family: 宋体;\">&nbsp; </span><span style=\"text-indent: 40px; font-family: 宋体;\">本会志愿服务活动接受民政局及其他政府有关部门的指导和监督。</span></p>\n<p style=\"text-indent: 144px; text-align: center;\"><span style=\"font-size:16px;font-family:宋体\">&nbsp;</span><strong><span style=\"font-size:16px;font-family:宋体\">第二章 志愿者服务组织</span></strong></p>\n<p><span style=\"font-size:16px;font-family:宋体\">&nbsp;&nbsp;&nbsp; </span><strong><span style=\"font-size:16px;font-family:宋体\">第七条</span></strong><span style=\"font-size:16px;font-family: 宋体\"> 本会志愿服务组织委员会：</span></p>\n<p style=\"text-indent: 32px\"><span style=\"font-size:16px;font-family:宋体\">（一）建立志愿服务活动的规章、制度；</span></p>\n<p style=\"text-indent: 32px\"><span style=\"font-size:16px;font-family:宋体\">（二）建立、健全志愿者和志愿服务活动的档案制度；</span></p>\n<p style=\"text-indent: 32px\"><span style=\"font-size:16px;font-family:宋体\">（三）志愿者的招募、培训、指导、管理、监督和表彰；</span></p>\n<p style=\"text-indent: 32px\"><span style=\"font-size:16px;font-family:宋体\">（四）组织开展志愿服务活动；</span></p>\n<p style=\"text-indent: 32px\"><span style=\"font-size:16px;font-family:宋体\">（五）志愿服务工作的宣传与交流；</span></p>\n<p style=\"text-indent: 32px\"><span style=\"font-size:16px;font-family:宋体\">（六）对志愿服务提供必要保障。</span></p>\n<p style=\"text-indent: 31px\"><strong><span style=\"font-size: 16px;font-family:宋体\">第八条</span></strong><span style=\"font-size:16px;font-family:宋体\"> 本会志愿服务相关人员开展志愿服务活动时，严格依照法律、法规以及志愿服务组织章程的规定，不从事营利性活动。</span></p>\n<p style=\"text-indent: 24px\"><span style=\"font-size:16px;font-family:宋体\">&nbsp;</span><strong><span style=\"font-size:16px;font-family:宋体\">第九条</span></strong><span style=\"font-size:16px;font-family: 宋体\"> 本会志愿服务组织可以自行或者联合招募志愿者。招募时，以适当的方式公告志愿服务计划。</span></p>\n<p style=\"text-indent: 32px\"><span style=\"font-size:16px;font-family:宋体\">志愿服务组织应当依照志愿服务计划开展志愿服务活动。志愿服务计划应当包括志愿者招募、培训、使用、考核及服务项目等事项。</span></p>\n<p style=\"text-indent: 32px\"><strong><span style=\"font-size:16px;font-family: 宋体\">第十条 </span></strong><span style=\"font-size:16px;font-family:宋体\">团体志愿者以集体形式开展志愿者服务活动的，应当将志愿服务计划及志愿服务活动情况向其加入的志愿者联备案。</span></p>\n<p style=\"text-indent: 32px\"><strong><span style=\"font-size: 16px;font-family:宋体\">第十一条</span></strong><span style=\"font-size:16px;font-family:宋体\"> 志愿服务组织的经费包括政府扶持、社会捐赠和资助及其他合法收入，专款专用。</span></p>\n<p style=\"text-indent: 32px\"><span style=\"font-size:16px;font-family:宋体\">志愿服务组织经费的筹集、使用和管理应当符合有关规定并接受政府有关部门和捐赠、资助者及志愿者的监督。经费的筹集、使用情况应当定期向社会公布。</span></p>\n<p style=\"margin-top:auto;margin-bottom: auto;text-align:center\"><strong><span style=\"font-size: 16px;font-family:宋体\">第三章 志愿者</span></strong></p>\n<p style=\"text-indent: 32px\"><strong><span style=\"font-size: 16px;font-family:宋体\">第十二条</span></strong><span style=\"font-size:16px;font-family:宋体\"> 本会志愿者应当具备下列条件：</span></p>\n<p style=\"text-indent: 40px\"><span style=\"font-size:16px;font-family:宋体\">（一）拥护本会志愿服务章程</span></p>\n<p style=\"text-indent: 40px\"><span style=\"font-size:16px;font-family:宋体\">（二）自愿从事志愿服务；</span></p>\n<p style=\"text-indent: 40px\"><span style=\"font-size:16px;font-family:宋体\">（三）具有相应的民事行为能力；</span></p>\n<p style=\"text-indent: 40px\"><span style=\"font-size:16px;font-family:宋体\">（四）符合志愿服务活动要求的身体条件；</span></p>\n<p style=\"text-indent: 40px\"><span style=\"font-size:16px;font-family:宋体\">（五）具有相应的服务能力。</span></p>\n<p style=\"margin: 15px 0;line-height: 23px\"><strong><span style=\"font-size:16px;font-family:宋体\">第十三条 </span></strong><span style=\"font-size:16px;font-family:宋体\">志愿服务组织建立志愿者登记制度。从事志愿服务的人员应当向志愿服务组织申请登记。</span><span style=\";font-family:宋体\">自愿从事义务服务的人员持申请书、身份证和职业证明及复印件向本会志愿者组织提出申请，</span><span style=\"font-family: 宋体\">提供自己的姓名、年龄、特长、职业、住址、联系方式及可参加活动的内容、时间等基本情况</span><span style=\";font-family:宋体\">；对符合志愿条件的个人或团体，填写《固安工业园区志愿者（团体）登记表》，并建立档案。</span></p>\n<p style=\"margin: 15px 0;line-height: 23px\"><span style=\"font-family: 宋体\">本会组织在确认申请人具备志愿者条件后，将通知申请人，并颁发本会志愿者证书、标志和志愿者序列号。</span></p>\n<p style=\"text-indent: 32px\"><span style=\"font-size:16px;font-family:宋体\">本会志愿服务组织可以根据志愿服务活动的需要，招募临时志愿者，但需在本会志愿服务委员会备案</span><span style=\"font-family: 宋体\">。</span></p>\n<p style=\"text-indent: 32px\"><span style=\"font-size:16px;font-family:宋体\">&nbsp;</span></p>\n<p><span style=\"font-size:16px;font-family:宋体\">&nbsp;&nbsp;&nbsp; </span><strong><span style=\"font-size:16px;font-family:宋体\">第十四条</span></strong><span style=\"font-size:16px;font-family: 宋体\"> 本会志愿者享有如下权利：</span></p>\n<p style=\"text-indent: 32px\"><span style=\"font-size:16px;font-family:宋体\">（一）自愿加入或者退出志愿服务组织；</span></p>\n<p style=\"text-indent: 32px\"><span style=\"font-size:16px;font-family:宋体\">（二）参加志愿服务组织开展的服务活动；</span></p>\n<p style=\"text-indent: 32px\"><span style=\"font-size:16px;font-family:宋体\">（三）要求获得志愿服务必需的条件和必要的保障；</span></p>\n<p style=\"text-indent: 32px\"><span style=\"font-size:16px;font-family:宋体\">（四）请求志愿服务组织帮助解决在志者服务期间遇到的实际困难；</span></p>\n<p style=\"text-indent: 32px\"><span style=\"font-size:16px;font-family:宋体\">（五）对志愿服务组织提出建议、批评与监督；</span></p>\n<p style=\"text-indent: 32px\"><span style=\"font-size:16px;font-family:宋体\">（六）有困难时优先得到志愿服务。</span></p>\n<p><span style=\"font-size:16px;font-family:宋体\">&nbsp;&nbsp; </span><strong><span style=\"font-size:16px;font-family:宋体\">第十五条 </span></strong><span style=\"font-size:16px;font-family: 宋体\">志愿者履行下列义务：</span></p>\n<p style=\"text-indent: 32px\"><span style=\"font-size:16px;font-family:宋体\">（一）遵守志愿服务组织的章程；</span></p>\n<p style=\"text-indent: 32px\"><span style=\"font-size:16px;font-family:宋体\">（二）不得向服务对象收取报酬或者借钱、借物、谋取其他利益；</span></p>\n<p style=\"text-indent: 32px\"><span style=\"font-size:16px;font-family:宋体\">（三）在服务期间不得接受服务对象的捐赠；</span></p>\n<p style=\"text-indent: 32px\"><span style=\"font-size:16px;font-family:宋体\">（四）对服务对象的隐私予以保密；</span></p>\n<p style=\"text-indent: 32px\"><span style=\"font-size:16px;font-family:宋体\">（五）不得以志愿者或者志愿服务组织的名义组织或者参与违反志愿服务原则的活动。</span></p>\n<p style=\"text-indent: 32px\"><strong><span style=\"font-size: 16px;font-family:宋体\">第十六条</span></strong><span style=\"font-size:16px;font-family:宋体\"> 本会志愿者应当在志愿服务组织的安排下开展志愿者服务，完成服务工作。</span></p>\n<p><span style=\"font-size:16px;font-family:宋体\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:16px;font-family:宋体\">未经本会志愿服务组织同意，任何单位和个人不得以志愿服务组织名义开展活动。</span></p>\n<p><span style=\"font-size:16px;font-family:宋体\">&nbsp;&nbsp;&nbsp; </span><strong><span style=\"font-size:16px;font-family:宋体\">第十七条</span></strong><span style=\"font-size:16px;font-family: 宋体\"> 本会志愿者服务组织保障志愿者在服务期间的合法权益。</span></p>\n<p style=\"text-indent: 32px\"><span style=\"font-size:16px;font-family:宋体\">志愿服务组织可以根据志愿服务的需要，为参加志愿服务活动的志愿者办理相应的人身保险。</span></p>\n<p><span style=\"font-size:16px;font-family:宋体\">&nbsp;&nbsp;&nbsp; </span><strong><span style=\"font-size:16px;font-family:宋体\">第十八条</span></strong><span style=\"font-size:16px;font-family: 宋体\"> 志愿者在从事志愿服务期间应当佩戴统一的志愿服务标志。</span></p>\n<p><span style=\"font-size:16px;font-family:宋体\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:16px;font-family:宋体\">志愿服务证件、标志的使用及管理办法由本会统一规定。</span></p>\n<p style=\"text-indent: 32px\"><span style=\"font-size:16px;font-family:宋体\">&nbsp;</span></p>\n<p style=\"text-indent: 209px; text-align: center;\"><strong><span style=\"font-size:16px;font-family:宋体\">第四章 志愿服务</span></strong></p>\n<p style=\"text-indent: 32px\"><strong><span style=\"font-size: 16px;font-family:宋体\">第十九条 </span></strong><span style=\"font-size:16px;font-family:宋体\">需要志愿服务的个人或者单位，可以向志愿服务组织提出服务申请。</span></p>\n<p style=\"text-indent: 32px\"><strong><span style=\"font-size: 16px;font-family:宋体\">第二十条</span></strong><span style=\"font-size:16px;font-family:宋体\"> 申请志愿服务，应当提交下列资料：</span></p>\n<p style=\"text-indent: 32px\"><span style=\"font-size:16px;font-family:宋体\">（一）志愿服务书面申请；</span></p>\n<p style=\"text-indent: 32px\"><span style=\"font-size:16px;font-family:宋体\">（二）申请人的身份证明或者单位证明；</span></p>\n<p style=\"text-indent: 32px\"><span style=\"font-size:16px;font-family:宋体\">（三）申请服务的项目说明及其它有关材料。</span></p>\n<p style=\"text-indent: 32px\"><span style=\"font-size:16px;font-family:宋体\">申请人为无民事行为能力、限制民事行为能力人的，由其监护人或者法定代理人代为申请。</span></p>\n<p style=\"text-indent: 24px\"><span style=\"font-size:16px;font-family:宋体\">&nbsp;</span><strong><span style=\"font-size:16px;font-family:宋体\">第二十一条</span></strong><span style=\"font-size:16px;font-family: 宋体\"> 本会志愿服务组织在受理服务申请后，可以根据服务对象的申请和实际情况，经确认后提供力所能及的志愿服务；不能提供志愿服务的，应当答复申请人。</span></p>\n<p style=\"text-indent: 16px\"><span style=\"font-size:16px;font-family:宋体\">&nbsp;</span><span style=\"font-size:16px;font-family:宋体\">提供志愿服务时，应当有两名以上志愿者参加。但服务项目有特别要求的除外。</span></p>\n<p><span style=\"font-size:16px;font-family:宋体\">&nbsp;&nbsp;&nbsp; </span><strong><span style=\"font-size:16px;font-family:宋体\">第二十二条</span></strong><span style=\"font-size:16px;font-family: 宋体\"> 志愿服务组织可以与服务对象签订服务协议，明确双方的权利和义务以及解决争议的方法。</span></p>\n<p style=\"text-indent: 32px\"><span style=\"font-size:16px;font-family:宋体\">在服务过程中，志愿服务组织和服务对象应当履行服务协议；一方要求变更或者解除协议的，可以由双方协商处理。</span></p>\n<p style=\"text-indent: 24px\"><strong><span style=\"font-size:16px;font-family:宋体\">&nbsp;</span></strong><strong><span style=\"font-size:16px;font-family:宋体\">第二十三条</span></strong><span style=\"font-size:16px;font-family:宋体\"> 服务对象应当根据服务协议的约定，按照服务的内容和特点，保障志愿者在安全的环境下开展服务。</span></p>\n<p style=\"text-indent: 24px\"><span style=\"font-size:16px;font-family:宋体\">&nbsp;</span><span style=\"font-size:16px;font-family:宋体\">服务对象可以根据自身实际情况，为参加服务的志愿者提供必要的专门培训和相应的物质保障。</span></p>\n<p style=\"text-indent: 225px; text-align: center;\"><strong><span style=\"font-size:16px;font-family:宋体\">第五章 表彰和鼓励</span></strong></p>\n<p style=\"text-indent: 32px\"><strong><span style=\"font-size: 16px;font-family:宋体\">第二十四条</span></strong><span style=\"font-size:16px;font-family:宋体\"> 志愿服务组织建立志愿者考核和表彰制度。</span></p>\n<p><span style=\"font-size:16px;font-family:宋体\">&nbsp; <strong>&nbsp;&nbsp;</strong></span><strong><span style=\"font-size:16px;font-family: 宋体\">第二十五条 </span></strong><span style=\"font-size:16px;font-family:宋体\">志愿服务组织对符合表彰规定的志愿者颁发志愿者荣誉证书。</span></p>\n<p style=\"text-indent: 32px\"><strong><span style=\"font-size: 16px;font-family:宋体\">第二十六条</span></strong><span style=\"font-size:16px;font-family:宋体\"> 鼓励有关单位在招工、招生时，在同等条件下优先录用、录取有志愿者服务经历者。具体鼓励办法可以由政府有关部门或者招工、招生单位另行规定。</span></p>\n<p><span style=\"font-size:16px;font-family:宋体\">&nbsp;&nbsp;&nbsp; </span><strong><span style=\"font-size:16px;font-family:宋体\">第二十七条</span></strong><span style=\"font-size:16px;font-family: 宋体\"> 新闻媒体应当无偿开展志愿服务的公益性宣传。对于服务表现突出的志愿者或者志愿服务组织，在征得其同意后，新闻媒体可以进行宣传。</span></p>\n<p style=\"text-indent: 189px; text-align: center;\"><strong><span style=\"font-size:16px;font-family:宋体\">第六章 法律责任</span></strong></p>\n<p><span style=\"font-size:16px;font-family:宋体\">&nbsp;&nbsp;&nbsp; </span><strong><span style=\"font-size:16px;font-family: 宋体\">第二十八条 </span></strong><span style=\"font-size:16px;font-family:宋体\">志愿者根据志愿服务组织的安排，在开展服务期间，造成服务对象或者第三人损害的，有关的志愿服务组织应当依法承担民事责任。但志愿服务组织与服务对象另有约定的除外。</span></p>\n<p><span style=\"font-size:16px;font-family:宋体\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:16px;font-family:宋体\">本会志愿服务组织承担民事责任后，有权向有故意或者重大过失的志愿者追偿部分或者全部赔偿费用。</span></p>\n<p><span style=\"font-size:16px;font-family:宋体\">&nbsp;&nbsp;&nbsp; </span><strong><span style=\"font-size:16px;font-family:宋体\">第二十九条 </span></strong><span style=\"font-size:16px;font-family:宋体\">服务对象在接受志愿者服务过程中对志愿者造成损害的，应当依法承担民事责任。志愿服务组织应当支持受损害的志愿者向有关的服务对象追偿损失，并提供必要的帮助。</span></p>\n<p><span style=\"font-size:16px;font-family:宋体\">&nbsp;&nbsp;&nbsp; </span><strong><span style=\"font-size:16px;font-family:宋体\">第三十条 </span></strong><span style=\"font-size:16px;font-family: 宋体\">冒用本会志愿服务组织的名义、标志和有关资料进行违法活动的，本会志愿服务组织有权要求有关机关依法追究其相应的法律责任。</span></p>\n<p style=\"text-indent: 32px\"><strong><span style=\"font-size: 16px;font-family:宋体\">第三十一条 </span></strong><span style=\"font-size:16px;font-family:宋体\">志愿服务人员在工作中有徇私舞弊、挪用公款等违法行为的，应当依法承担相应的法律责任；构成犯罪的，由司法机关依法追究其刑事责任。</span></p>\n<p style=\"margin-top:auto;margin-bottom: auto;text-align:center;text-indent:40px\"><strong><span style=\"font-size:16px;font-family: 宋体\">第七章 附则</span></strong></p>\n<p style=\"text-indent: 40px\"><strong><span style=\"font-size:16px;font-family: 宋体\">第三十二条</span></strong><span style=\"font-size:16px;font-family:宋体\"> 本章程适用于固安产业新城范围内。</span></p>\n<p style=\"text-indent: 40px\"><strong><span style=\"font-size:16px;font-family: 宋体\">第三十三条 </span></strong><span style=\"font-size:16px;font-family:宋体\">本章程自下发之日起开始实施。</span></p>\n<p><br></p>', null, null, null, 'f24fe1ed2f6f4e1ebb11ffb7c2a3eb36', '2017-04-14 15:39:39', null, null, null, '1', null, null, '1', null);
INSERT INTO `update_cms_contente` VALUES ('c_100208-s_10008', '第三章', null, null, '<p><span style=\"color: rgb(51, 51, 51); font-family: 微软雅黑; font-size: 12px; line-height: 26px; widows: 1;\">第一条 根据《中华人民共和国税收征收管理法》及《中华人民共和国税收征收管理法实施细则》和国务院《社会团体登记管理条例》的有关规定，制定本章程。 第二条 中国注册税务师协会（英文名称：The China Certified Tax Agents Association，缩写CCTAA），是由中国注册税务师和税务师事务 所组成的独立、民间和自律的全国性一级社会团体，受民政部和国家税务总局的指导和监督。中国注册税务师协会（以下简称本会）经民政部注册登记。第三条 本会的宗旨是服务会员，建立与完善会员职业道德规范和执业准则，监督实施注册税务师行业自律管理，协调行业内、外部关系，维护国家和社会公众利益，维 护纳税人和会员合法权益，促进行业健康发展。第四条 本会设在北京市。</span></p>', null, null, null, 'f24fe1ed2f6f4e1ebb11ffb7c2a3eb36', '2017-04-14 16:09:19', null, null, null, '0', null, null, '3', null);
